  border-color: transparent;
  transition: var(--transition);
  cursor: pointer;
}

.nav-toggle.nav-toggle-clicked {
  color: grey;
  transform: rotate(90deg);
}

.logo {
  height: 60px;
}

.links-container {
  height: 0;
  overflow: hidden;
  transition: var(--transition);
}

.show-container {
  height: 10rem;
}

.links .link {
  color: #fff;
  font-weight: 400;
  display: block;
  font-size: 1.25rem;
  padding: 0.5rem 1rem;
  transition: var(--transition);
  padding: 0.5rem 1.5rem;
  /* text-align: center; */
}

.links .link:hover {
  background: lightgray;
  /* background-color: lightgray; */
  color: white;
  padding: 0.5rem 1rem;
}

@media screen and (min-width: 760px) {
  .nav-center {
    max-width: 1170px;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 1rem;
  }

  .nav-header {
    padding: 0;
  }

  .nav-toggle {
    display: none;
  }

  .links-container {
    height: auto !important;
  }

  .links {
    display: flex;
  }

  .links .link {
    padding: 0;
    margin: 0 0.2rem;
    border: 1px solid transparent;
    border-radius: 0.75rem;
    padding: 0.5rem 1rem;
    font-size: 1.15rem;
    font-weight: 400;
  }

  /* .links .link:hover {
    padding: 0;

  } */

  .links .link:hover {
    background: lightgray;
    padding: 0.5rem 1rem;
    color: var(--clr-primary-1);
  }
}

.page {
  height: 100vh;
}
