commits.content:
.hero {
  /* display: flex; */
  /* flex-direction: column-reverse; */
  position: relative;
  margin-bottom: 2rem;
  height: 44vh;
}

.info {
  color: var(--clr-primary-1);
  width: 100%;
  height: 100%;
  background-color: hsla(0, 0%, 0%, 0.5);
  z-index: 1;
  font-weight: 900;
  text-align: center;
  position: absolute;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.info h2 {
  margin-top: -1rem;
}

.info .info-dot {
  color: var(--clr-primary-1);
  font-size: 2rem;
}

.info button {
  margin-top: 1.5rem;
}

.bio p {
  color: var(--clr-primary-1);

  margin: 2rem;
}

.image-container {
  height: 44vh;
}

.image-hero {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.partners {
  margin: 2rem;
  text-align: center;
  color: var(--clr-primary-1);
}

.partners h3 {
  margin-bottom: 1rem;
}

.logo-partners-container {
  display: flex;
  flex-direction: column;
  /* max-height: 100px; */
  /* height: 50%; */
  /* width: 80%; */
  justify-content: space-around;
  align-items: center;
  align-content: center;
}

.logo-partners-container div {
  height: 40px;
  margin: 1.25rem 0;
}

.logo-partners-container div img {
  height: 100%;
  object-fit:cover;
}

.logo-partners-container .certificate1 {
  height: 70px;
}

/* image on the right  */
@media (min-width: 760px) {
  .hero {
    display: flex;
    flex-direction: row;
    margin-top: 1rem;
    margin-bottom: 2rem;
    height: 400px;
  }

  .info {
    position: static;
    width: 60%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    /* align-items: center; */
    align-content: center;
    color: white;
    background-color: transparent;
  }

  /* .info > * {
    margin-bottom: 20px;
  } */

  .info h1 {
    font-weight: 800;
    font-size: 60px;
  }

  .info h2 {
    margin-top: 0;
  }

  .info h3 {
    font-size: 30px;
    margin-top: 1rem;
    line-height: 1.4;
  }

  .info-dot {
    color: var(--clr-primary-1);
    font-weight: 900;
  }

  .bio {
    padding: 0 10%;
  }

  .bio p {
    margin: 4rem 0;
    font-weight: 500;
  }

  .image-container {
    width: 80%;
    height: 100%;
  }

  .image-hero {
    max-width: 100%;
    height: 100%;
    object-fit: cover;
    /* border-radius: 5%; */
    clip-path: polygon(0% 0, 100% 0, 100% 100%, 40% 100%);
  }

  .button-hero {
    color: white;
  }

  .partners {
    margin: 4rem 0;
    background-color: transparent;
    color: var(--clr-primary-1);
  }

  .logo-partners-container {
    flex-direction: row;
  }
}
