commits.content:
.section-services {
  margin: 2rem 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  /* color: var(--clr-primary-1); */
}

.container-services {
  /* display: flex; */
  padding: 0 2rem;
}

.service {
  padding: 1rem;
  text-align: center;
}

.service ul {
  list-style-type: disc;
  list-style-position: inside;
}

.service li {
  margin-bottom: 0.5rem;
  list-style-type: disc;
  /* text-align: left; */
}

@media (min-width: 800px) {
  .section-services {
    margin: 4rem 0;
    display: flex;
    flex-direction: column;
    align-items: center;
    color: var(--clr-primary-1);
    width: 95vw;
  }

  .section-services h3 {
    margin-bottom: 1rem;
  }

  .section-services h4 {
    margin-bottom: 1.5rem;
  }

  .container-services {
    display: flex;
    padding: 0 4rem;
  }

  .service {
    padding: 1rem;
    text-align: center;
    width: 30vw;
  }
}
